// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faPenToSquare } from "@fortawesome/free-regular-svg-icons" ;
// import { faTrashCan } from "@fortawesome/free-regular-svg-icons" ;
import axios from "axios";
import React, { useEffect, useState } from "react";

export default function EmployeeDetails(){

    const [empInfo, setEmpInfo] = useState([]);

    useEffect(() => {
        const fetchUser = async() => {
            const employeeId = window.localStorage.getItem("id");
            try{
                const {data:response} = await axios.get(`http://localhost:5000/Employee_Details?id=${employeeId}`)
                const welcomeMessage = document.getElementById("employeeDashboard");
                const heading = document.getElementsByClassName("formHeading");
                if(heading.length === 0){
                    const headingDiv = document.createElement("h4");
                    headingDiv.classList.add("formHeading", "mb-4");
                    headingDiv.style.float = "right";
                    var text = document.createTextNode("Welcome "+ response[0].FirstName + response[0].LastName + " (" + response[0].id + ")");
                    headingDiv.appendChild(text);
                    welcomeMessage.appendChild(headingDiv);
                }
                setEmpInfo(response);
            }catch(error) {
                console.error(error.message);
            }
        }
        fetchUser();
    }, [])
    return(
        <>
            <div className="row">
                <p id="employeeDashboard" style={{float:"right"}}></p>
                {/* <div className="col-1"></div> */}
                <div className="col-5">
                    <div className="card" style={{width : "18rem"}}>
                        <div className="card-body">
                            <h5 className="card-title">Employee Information</h5>
                            <h6 className="card-subtitle mb-2 text-muted">Personal</h6>
                            {
                                empInfo.map((emp,index) => (
                                    <ul style={{listStyle: "none"}}>
                                        <li>{emp.FirstName}</li>
                                        <li>{emp.LastName}</li>
                                        <li>{emp.Email}</li>
                                        <li>{emp.Designation}</li>
                                        <li>{emp.Gender}</li>
                                        <li>{emp.BaseLocation}</li>
                                        <li>{emp.Role}</li>
                                        <li>{emp.Salary}</li>
                                    </ul>
                                ))
                            }
                            
                            <h6 className="card-subtitle mb-2 text-muted">Employment</h6>
                            <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
