import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPenToSquare } from "@fortawesome/free-regular-svg-icons" ;
import { faTrashCan } from "@fortawesome/free-regular-svg-icons" ;
import axios from "axios";

export default function AdminDashboard(){

    const[editInfo, setEditInfo] = useState(false);
    const[empinfo, setEmpInfo] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:5000/Employee_Details')
        .then((res) => {
            setEmpInfo(res.data);
            console.log(res.data);
        })
        .catch((error) => {
            console.log(error);
        })
    }, [])

    return(
        <>
            <div className="row">
                <div className="col-1"></div>
                <div className="col-10">
                    <h4 className="formHeading mb-4">Employee Information</h4>
                    <table className="table table-striped table-primary">
                        <tr>
                            <th>ID</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email Id</th>
                            <th>Designation</th>
                            <th>Gender</th>
                            <th>Role</th>
                            <th>Base Location</th>
                            <th>Salary</th>
                        </tr>
                        {
                            empinfo.map((emp,index) => (
                                <tr>
                                    <td>{emp.id}</td>
                                    <td>{emp.FirstName}</td>
                                    <td>{emp.LastName}</td>
                                    <td>{emp.Email}</td>
                                    <td>{emp.Designation}</td>
                                    <td>{emp.Gender}</td>
                                    <td>{emp.BaseLocation}</td>
                                    <td>{emp.Role}</td>
                                    <td>{emp.Salary}</td>
                                    <td>
                                        <FontAwesomeIcon icon={faPenToSquare} onClick={(e) => setEditInfo([...editInfo, true])}/>   
                                            {
                                                <>
                                                    
                                                </>
                                            }     
                                        <FontAwesomeIcon icon={faTrashCan} />
                                    </td>
                                </tr>
                            ))
                        }
                    </table>
                </div>
            </div>
        </>
    );
}
