import './App.css';
import Login from './Components/Login';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Logout from './Components/Logout';
import EmployeeDetails from './Components/EmployeeDetails';
import AdminDashboard from './Components/AdminDashboard';
import LeaveRequests from './Components/LeaveRequests';
import AddEmployee from './Components/AddEmployee';
import NavigationBar from './Components/NavigationBar';
//import ProtectedRoute from './Components/ProtectedRoute';
import {user, loggedIn} from './Components/Context';

function App() {
  const isLoggedIn = window.localStorage.getItem("isloggedIn");
  const role = window.localStorage.getItem("userType");

  return (
    <BrowserRouter>
      <div className='container-fluid'>
        <loggedIn.Provider value={isLoggedIn}>
          <user.Provider value = {role}>
            <NavigationBar/>
          </user.Provider>
        </loggedIn.Provider>


        <Routes>

            <>
              <Route path="/" exact Component={Login}/>
              <Route path="/login" exact Component={Login}/>
            </>

          {/* <Route exact Component={ProtectedRoute}> */}
              <Route path="/" exact Component={<Navigate to="/" />}/>
              <Route path="/login" exact Component={<Navigate to="EmployeeDetails" />}/>
              <Route path="/employeeDetails" exact Component={EmployeeDetails}/>
              <Route path="/admin-dashboard" exact Component={AdminDashboard}/>
              <Route path="/leaveRequests" exact Component={LeaveRequests}/>
              <Route path="/addEmployee" exact Component={AddEmployee}/>
              <Route path="/logout" exact Component={Logout}/>
          {/* </Route>   */}
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
