import React, { useState } from "react";
import axios from "axios";

export default function AddEmployee(){
    // const [firstName, setFirstName] = useState("");
    // const [lastName, setLastName] = useState("");
    // const [emailId, setEmailId] = useState("");
    // const [employeeId, setEmployeeId] = useState("");
    // const [employeeDesignation, setEmployeeDesignation] = useState("");
    // const [gender, setGender] = useState("");
    // const [baseLoc, setBaseLoc] = useState("");
    // const [role, setRole] = useState("");
    // const [tempPwd, setTempPwd] = useState("");
    function createRandomString(length) {
        const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        let result = "";
        for (let i = 0; i < length; i++) {
          result += chars.charAt(Math.floor(Math.random() * chars.length));
        }
        return result;
    }

    const [addEmpInfo, setAddEmpInfo] = useState({
        "firstName" : "",
        "lastName" : "",
        "emailId" : "",
        "employeeId" : "",
        "employeeDesignation" : "",
        "gender" : "",
        "baseLoc" : "",
        "role" : "",
        "salary" : 0.0,
        "tempPwd" : ""
    })

    function AddEmployee(e){
        e.preventDefault();
        const encodedValues = [
            addEmpInfo.firstName,
            addEmpInfo.lastName,
            addEmpInfo.emailId,
            addEmpInfo.employeeId,
            addEmpInfo.employeeDesignation,
            addEmpInfo.gender,
            addEmpInfo.baseLoc,
            addEmpInfo.role,
            createRandomString(8),
            addEmpInfo.salary
        ];

        axios.post(`http://localhost:5000/Employee_Details`, encodedValues)
        .then((data) => 
            {
                var submissionResponse = document.getElementById("submissionResponse");
                var validationResult = document.getElementById("validationResult");
                if(validationResult !== null){
                    submissionResponse.removeChild(document.getElementById("validationResult"));
                }
                if(data.length === 0){
                    submissionResponse.insertAdjacentHTML("afterend", `<span id="validationResult">Employee Registration Failed!</span>`);
                    submissionResponse.style.display = "block";
                }
                else{
                    submissionResponse.insertAdjacentHTML("beforeend", `<span id="validationResult">Employee with Id: ${data.id} successfully Created!</span>`);
                    submissionResponse.style.display = "block";
                }
            }
        )
        .catch((error) => {
            console.error(error);
        });
    }
    return(
        <>
            <div className="row">
                <div className="col-3"></div>
                <div className="col-6 formSection">
                    <h4 className="mt-4 mb-4 formHeading">Add New Employee</h4>
                    <form className="formFieldsAlign" method="POST" onSubmit={AddEmployee}>
                        <input type="text" className="form-control mb-3" placeholder="First Name" name="firstName"
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, firstName:e.target.value})}}/>
                        <input type="text" className="form-control mb-3" placeholder="Last Name" name="lastName"
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, lastName:e.target.value})}}/>
                        <input type="email" className="form-control mb-3" placeholder="Email" name="emailId"
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, emailId:e.target.value})}}/>
                        <input type="text" className="form-control mb-3" placeholder="Employee Id" name="employeeId"
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, employeeId:e.target.value})}}/>
                        <input type="text" className="form-control mb-3" placeholder="Employee Designation" name="employeeDesignation"
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, employeeDesignation:e.target.value})}}/>
                        <input type="password" className="form-control mb-3" placeholder="Temporary Password" name="tempPwd" disabled
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, tempPwd:createRandomString(8)})}}/>
                        <input type="text" className="form-control mb-3" placeholder="Salary" name="salary"
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, salary:e.target.value})} }/>
                            <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"
                            onChange={(e) => {setAddEmpInfo({...addEmpInfo, gender:e.target.value})} }/>
                            <label className="form-check-label">
                                Male
                            </label>
                            <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2"
                            onChange={(e) => {setAddEmpInfo({...addEmpInfo, gender:e.target.value})} }/>
                            <label className="form-check-label">
                                Female
                            </label>
                            <br/>
                        <select name="baseLoc" className="form-control mb-3" 
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, baseLoc:e.target.value})} }> 
                            <option value="-1">Select Base Location</option>
                            <option value="Hyderabad">Hyderabad</option>
                            <option value="Bengaluru">Bengaluru</option>
                            <option value="Chennai">Chennai</option>
                        </select>
                        <label>Employee Role</label>
                        <select name="role" className="form-control mb-3" 
                        onChange={(e) => {setAddEmpInfo({...addEmpInfo, role:e.target.value})} }> 
                            <option value="-1">Select Role</option>
                            <option value="Hyderabad">Non_Admin</option>
                            <option value="Bengaluru">Admin</option>
                        </select>
                        <p><button type="submit" name="addEmployee" className="btn btn-primary mt-3">Add Employee</button></p>
                        <p id="submissionResponse"></p>
                    </form>
                </div>
            </div>
        </>
    );
}
