import React, { useState } from "react";

export default function Login() 
{
    const [id, setId] = useState("");
    const [password, setpassword] = useState("");

    function validateLogin(e){
        e.preventDefault();
        //console.log(id,password);
        const encodedValue = encodeURIComponent(id);
        fetch(`http://localhost:5000/Employee_Details?id=${encodedValue}`, {
            method : 'GET',
            crossDomain : true,
        })
        .then((res) => res.json())
        .then((data) => {
            var loginError = document.getElementById("errorMessage");
            var validationError = document.getElementById("validationError");
            if(validationError !== null){
                loginError.removeChild(document.getElementById("validationError"));
            }
           
            if(data.length === 0){
                loginError.insertAdjacentHTML("afterend", `<span id="validationError">No Profile Found!</span>`);
                loginError.style.display = "block";
            }
            else if(data[0].password !== password){
                loginError.insertAdjacentHTML("beforeend", `<span id="validationError">Wrong Password!</span>`);
                loginError.style.display = "block";
            }
            else{
                window.localStorage.setItem("token", data[0]);
                window.localStorage.setItem("userType", data[0].Role);
                window.localStorage.setItem("loggedIn", true);

                if(Role === "Admin"){
                    return(window.location.href = './admin-dashboard');
                }
                else{
                    window.location.href = './employeeDetails';
                }
            }
        })
    }

    return(
        <>
            <div className="row loginBackgroundImage">
                <div className="col-1"></div>
                <div className="col-4 formSection">
                    <h4 className="mt-4 mb-4 formHeading">Welcome to HR Portal</h4>
                    <h4 className="mt-4 mb-4 formHeading">Login</h4>
                    <form className="formFieldsAlign" onSubmit={validateLogin}>
                        <input type="text" name="loginId" placeholder="Employee Id" className="form-control"
                        onChange={(e) => setId(e.target.value)}/>
                        <br/>
                        <input type="password" name="loginPassword" placeholder="Password" className="mb-3 form-control"
                        onChange={(e) => setpassword(e.target.value)}/>
                        <p id="errorMessage"></p>
                        <p><button type="submit" name="login" className="btn btn-primary mt-3">Login</button></p>
                    </form>
                </div>
                <div className="col-1"></div>
                <div className="col-5">
                    <img src= '..\Images\HR_Portal_Launch.jpg' alt="Hr Portal Responsibilities"
                    className="login_sideNav_img"></img>
                </div>
            </div>
        </>
    );
}
