// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faPenToSquare } from "@fortawesome/free-regular-svg-icons" ;
// import { faTrashCan } from "@fortawesome/free-regular-svg-icons" ;
import axios from "axios";
import React, { useEffect, useState } from "react";

export default function EmployeeDetails(){

    const [empInfo, setEmpInfo] = useState([]);

    useEffect(() => {
        const fetchUser = async() => {
            const employeeId = window.localStorage.getItem("id");
            try{
                const {data:response} = await axios.get(`http://localhost:5000/Employee_Details?id=${employeeId}`)
                const welcomeMessage = document.getElementById("employeeDashboard");
               welcomeMessage.innerText = "Welcome "+ response[0].FirstName + response[0].LastName + " (" + response[0].id + ")";
                    //const headingDiv = document.createElement("h4");
                    //headingDiv.classList.add("formHeading", "mb-4");
                    //headingDiv.style.float = "right";
                    //var text = document.createTextNode("Welcome "+ response[0].FirstName + response[0].LastName + " (" + response[0].id + ")");
                    //headingDiv.appendChild(text);
                    //welcomeMessage.appendChild(headingDiv);
               
                setEmpInfo(response);
            }catch(error) {
                console.error(error.message);
            }
        }
        fetchUser();
    }, [])
    return(
        <>
            <div className="row">
                <h4 id="employeeDashboard" className="formHeading mb-4" style={{float:"right"}}>Welcome</h4>
                {/* <div className="col-1"></div> */}
                <div className="col-5">
                    <div className="card">
                        <div className="card-body">
                            <h4 className="mt-4 mb-4 formHeading">Personal Details</h4>
                                <table>
                                {
                                        <tbody>
                                            <tr>
                                                <td>First Name</td>
                                                <td>{empInfo.FirstName}</td>
                                            </tr> 
                                            <tr key="2">
                                                <td>Last Name</td>
                                                <td>{emp.LastName}</td>
                                            </tr>
                                            <tr key="3">
                                                <td>Personal Email</td>
                                                <td>{emp.Email}</td>
                                            </tr> 
                                            <tr key="4">
                                                <td>Designation</td>
                                                <td>{emp.Designation}</td>
                                            </tr> 
                                            <tr key="5">
                                                <td>Base Location</td>
                                                <td>{emp.BaseLocation}</td>
                                            </tr> 
                                            <tr key="6">
                                                <td>Gross Salary</td>
                                                <td>{emp.Salary}</td>
                                            </tr> 
                                            <tr key="7">
                                                <td>Joining Date</td>
                                                <td>{emp.JoiningDate}</td>
                                            </tr>  
                                        </tbody>
                                }
                                </table>
                        </div>
                    </div>
                </div>
                <div className="col-6">
                    <h4 className="mt-4 mb-4 formHeading">Personal Details</h4>
                </div>
            </div>
        </>
    );
}
