import axios from "axios";
import React, { useEffect, useState } from "react";

export default function LeaveRequests(){
    const [allLeaveInfo, setAllLeaveInfo] = useState([]);
    const [leaveApproveEmp, setLeaveApproveEmp] = useState([]);
    //const [counter, setCounter] = useState(0);
    const employeeId = window.localStorage.getItem("id");

    useEffect(() => {
        axios.get('http://localhost:5500/Leave_Details')
        .then((res) => {
            console.log(res.data);
            setAllLeaveInfo(res.data);
        })
        .catch((error) => {
            console.log(error);
        })
    }, []);

    function ApproveLeaveRequest(reqId, reqStatus){
        reqStatus === "cancel" ? 
            leaveApproveEmp.Requests.splice(reqId, 1)
            :
            leaveApproveEmp.Requests[reqId].Status = reqStatus;
        axios.put(`http://localhost:5500/Leave_Details/${leaveApproveEmp.id}`, leaveApproveEmp)
        .then((res) => {
            window.location.reload();
        })
        .catch((error) => {
            console.log(error);
        })
    }

    // function CancelLeaveRequest(cancelId){
    //     axios.delete(`http://localhost:5500/Leave_Details/${leaveApproveEmp.id}/`)
    // }

    return(
        <>
            <div className="row">
                <div className="col-1"></div>
                <div className="col-9">
                    <h4 className="formHeading mb-4">Employee Leave Requests</h4>
                    <table className="table table-striped table-primary">
                        <tbody>
                            <tr>
                                {/* <th>S.No</th> */}
                                <th>Employee ID</th>
                                <th>Leave Type</th>
                                <th>Reason</th>
                                <th></th>
                            </tr>
                            {
                                allLeaveInfo.map((empLeave, index) => (
                                    empLeave.Manager === employeeId &&
                                    empLeave.Requests.map((request, reqIndex) => (
                                        request.Status !== "Approved" && 
                                        <tr key={reqIndex}>
                                            <td></td>
                                            <td>{empLeave.id}</td>
                                            <td>{request.LeaveType}</td>
                                            <td>{request.Reason}</td>
                                            <td>
                                                    <button
                                                        type="button" 
                                                        name="approve" 
                                                        className="btn btn-primary me-3" onClick={() => {ApproveLeaveRequest(request.LeaveId, "Approved"); setLeaveApproveEmp(empLeave)}}>Approve
                                                    </button>
                                                    <button
                                                        type="button" 
                                                        name="reject" 
                                                        className="btn btn-primary me-3" onClick={() => {ApproveLeaveRequest(request.LeaveId, "Rejected"); setLeaveApproveEmp(empLeave)}}>Reject
                                                    </button>
                                            </td>
                                        </tr>
                                    ))
                                ))
                            }
                        </tbody>
                    </table>
                    <h4 className="formHeading mb-4">My Leave Requests</h4>
                    <table className="table table-striped table-primary">
                        <thead>
                            <tr>
                                {/* //<th>S.No</th> */}
                                <th>Leave Type</th>
                                <th>Reason</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                allLeaveInfo.map((myLeave, index) => (
                                    myLeave.id === employeeId &&
                                    myLeave.Requests.map((request, requestId) => (
                                        <tr key={requestId}>
                                            <td></td>
                                            <td>{request.LeaveType}</td>
                                            <td>{request.Reason}</td>
                                            <td>
                                                {
                                                    request.Status === "Pending" &&
                                                    <button
                                                        type="button" 
                                                        name="cancel" 
                                                        className="btn btn-primary" onClick={() => {ApproveLeaveRequest(request.LeaveId, "Cancel"); setLeaveApproveEmp(myLeave)}}>Cancel
                                                    </button>
                                                }
                                            </td>
                                        </tr>
                                    ))
                                    
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </div>
            
        </>
    );
}
