import React, { useState } from "react";

export default function AddEmployee(){
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [emailId, setEmailId] = useState("");
    const [employeeId, setEmployeeId] = useState("");
    const [employeeDesignation, setEmployeeDesignation] = useState("");
    const [gender, setGender] = useState("");
    const [baseLoc, setBaseLoc] = useState("");
    const [role, setRole] = useState("");
    const [tempPwd, setTempPwd] = useState("");
    let pass = n=> [...crypto.getRandomValues(new Uint8Array(n))]
   .map((x,i)=>(i=x/255*61|0,String.fromCharCode(i+(i>9?i>35?61:55:48)))).join``

    function AddEmployee(e){
        e.preventDefault();
        const encodedValues = {
            firstName : encodeURIComponent(firstName),
            lastName : encodeURIComponent(lastName),
            emailId : encodeURIComponent(emailId),
            employeeId : encodeURIComponent(employeeId),
            employeeDesignation : encodeURIComponent(employeeDesignation),
            gender : encodeURIComponent(gender),
            baseLocation : encodeURIComponent(baseLoc),
            role : encodeURIComponent(role),
            tempPwd : encodeURIComponent(tempPwd)
        }
        fetch(`http://localhost:5000/Employee_Details`, {
            method : "POST",
            crossDomain : true,
            body:{
                "FirstName" : encodedValues[0],
                "LastName" : encodedValues[1],
                "Email" : encodedValues[2],
                "id" : encodedValues[3],
                "Designation" : encodedValues[4],
                "Gender" : encodedValues[5],
                "BaseLocation" : encodedValues[6],
                "Role" : encodedValues[7],
                "Password" : encodedValues[8]
            }
        })
        .then((res) => res.json())
        .then((data) => {
            {
                var submissionResponse = document.getElementById("submissionResponse");
                var validationResult = document.getElementById("validationResult");
                if(validationResult !== null){
                    submissionResponse.removeChild(document.getElementById("validationResult"));
                }
                if(data.length === 0){
                    submissionResponse.insertAdjacentHTML("afterend", `<span id="validationResult">Employee Registration Failed!</span>`);
                    submissionResponse.style.display = "block";
                }
                else{
                    submissionResponse.insertAdjacentHTML("beforeend", `<span id="validationResult">Employee with Id: ${data.id} successfully Created!</span>`);
                    submissionResponse.style.display = "block";
                }
            }
        })
        .catch((error) => {
            console.error(error);
        });
    }
    return(
        <>
            <div className="row">
                <div className="col-2"></div>
                <div className="col-6 formSection">
                    <h4 className="mt-4 mb-4 formHeading">Add New Employee</h4>
                    <form className="formFieldsAlign" onSubmit={AddEmployee}>
                        <input type="text" className="form-control" placeholder="First Name" name="firstName"
                        onChange={(e) => setFirstName(e.target.value)}/>
                        <input type="text" className="form-control mb-3" placeholder="Last Name" name="lastName"
                        onChange={(e) => setLastName(e.target.value)}/>
                        <input type="email" className="form-control mb-3" placeholder="Email" name="emailId"
                        onChange={(e) => setEmailId(e.target.value)}/>
                        <input type="text" className="form-control mb-3" placeholder="Employee Id" name="employeeId"
                        onChange={(e) => setEmployeeId(e.target.value)}/>
                        <input type="text" className="form-control mb-3" placeholder="Employee Designation" name="employeeDesignation"
                        onChange={(e) => setEmployeeDesignation(e.target.value)}/>
                        <input type="password" className="form-control mb-3" placeholder="Temporary Password" name="tempPwd" value={pass} disabled
                        onChange={(e) => setTempPwd(e.target.value)}/>
                        <div class="form-check">
                            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"
                            onChange={(e) => setGender(e.target.value)}/>
                            <label class="form-check-label" for="flexRadioDefault1">
                                Male
                            </label>
                        </div>
                        <div class="form-check">
                            <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2"
                            onChange={(e) => setGender(e.target.value)}/>
                            <label class="form-check-label" for="flexRadioDefault2">
                                Female
                            </label>
                        </div>
                        <label>Location</label>
                        <select name="baseLoc" className="form-control mb-3" onChange={(e) => setBaseLoc(e.target.value)}> 
                            <option value="-1">Select City</option>
                            <option value="Hyderabad">Hyderabad</option>
                            <option value="Bengaluru">Bengaluru</option>
                            <option value="Chennai">Chennai</option>
                        </select>
                        <label>Employee Role</label>
                        <select name="role" className="form-control mb-3" onChange={(e) => setRole(e.target.value)}> 
                            <option value="-1">Select Role</option>
                            <option value="Hyderabad">Non_Admin</option>
                            <option value="Bengaluru">Admin</option>
                        </select>
                        <p><button type="submit" name="addEmployee" className="btn btn-primary mt-3">Add Employee</button></p>
                        <p id="submissionResponse"></p>
                    </form>
                </div>
            </div>
        </>
    );
}
