import React, { useState } from "react";

export default function Login(){

    const [email, SetEmail] = useState("");
    const [pwd, SetPwd] = useState("");

    function handleSubmit(e){
        e.preventDefault();

        fetch("http://localhost:5000/Employee_Details", {
            method : "GET",
            crossDomain : true
        })
        .then((res) => res.json)
        .then((data) => {
            console.log(data);
            if(data.status === "ok"){
                console.log(data.Role);
                window.localStorage.setItem("token", data.data);
                window.localStorage.setItem("Role", data.Role);
                window.localStorage.setItem("loggedIn", true);
            }
        })
    }

    return(
        <>
            <div className="row loginBackgroundImage">
                <div className="col-4"></div>
                <div className="col-4 formSection">
                    <h4 className="mt-4 mb-4 formHeading">Login</h4>
                    <form className="formFieldsAlign" onSubmit={handleSubmit}>
                        <input type="text" name="loginName" placeholder="Username" className="form-control"
                            onChange={(e)=>SetEmail(e.target.value)}/>
                        <br/>
                        <input type="password" name="loginPassword" placeholder="Password" className="mb-3 form-control"
                            onChange={(e) => SetPwd(e.target.value)}/>
                        <p><button type="submit" name="login" className="btn btn-primary">Login</button></p>
                    </form>
                </div>
            </div>
        </>
    )
}
