import axios from "axios";
import React, { useEffect, useState } from "react";

export default function EmployeeDetails(){

    const [empInfo, setEmpInfo] = useState([]);

    useEffect(() => {
        const fetchUser = async() => {
            const employeeId = window.localStorage.getItem("id");
            try{
                const {data:response} = await axios.get(`http://localhost:5000/Employee_Details?id=${employeeId}`)
                const welcomeMessage = document.getElementById("empDashboardWelcomeMessage");
                const headingDiv = document.createElement("h4");
                headingDiv.classList("formHeading mb-4")
                headingDiv.innerHTML("Welcome "+ response[0].FirstName);
                setEmpInfo(response);
                welcomeMessage.insertAdjacentText("Welcome "+ response[0].FirstName);
            }catch(error) {
                console.error(error.message);
            }
        }
        fetchUser();
    }, [])
    return(
        <>
            {/* <h4 className="formHeading mb-4" id="empDashboardWelcomeMessage"></h4> */}
        </>
    );
}
